<?php get_header();?><!-- inicio conteudo --><section class="conteudo">	<?php if(is_home()): ?>	<h2>&Uacute;ltimas not&iacute;cias do blog</h2>	<hr />	<?php endif;?>	<?php if(is_tag()): ?>	<h2>Tag</h2>	<hr />	<?php endif;?>	<?php if(is_category()): ?>	<h2>		<?php echo term_description();?>	</h2>	<hr />	<?php endif;?>	<?php if ( have_posts() ) : while ( have_posts() ) : the_post(); ?>	<article id="post-<?php echo $post->ID; ?>" class="post">		<h2>			<a				title="<?php _e('Leia', 'axfordummies');?>&nbsp;<?php the_title(); ?>"				href="<?php the_permalink() ?>" rel="bookmark"><?php the_title(); ?>			</a>		</h2>		<p>			<?php _e('Publicado em','axfordummies');?>			&nbsp;			<?php the_time('d/m/Y');?>			&nbsp;			<?php _e('por','axfordummies');?>			&nbsp;			<?php the_author();?>		</p>		<?php if( is_home() || is_search() ): ?>		<?php the_excerpt(__('Continue lendo &rarr;','axfordummies')); ?>		<?php elseif (is_single() || is_page()): ?>		<?php the_content(__('Continue lendo &rarr;', 'axfordummies')); ?>		<?php else: ?>		<?php the_excerpt(__('Continue lendo &rarr;','axfordummies')); ?>		<?php endif; ?>		<p>			<?php comments_popup_link(__('<span><strong>0</strong> Coment&aacute;rios</span>','axfordummies'), __('<span><strong>1</strong> Coment&aacute;rio</span>','axfordummies'), __('<span><strong>%</strong> Coment&aacute;rios</span>','axfordummies')); ?>		</p>	</article>	<?php endwhile; ?>	<?php if(function_exists('wp_page_numbers')) { 		wp_page_numbers();} ?>	<?php else : ?>	<h2>N&atilde;o encontrado</h2>	<p>		<?php _e("Descupe, mas voc&ecirc; est&aacute; procurando por algo que n&atilde;o est&aacute; aqui.",'axfordummies'); ?>	</p>	<?php endif;?></section><!-- fim conteudo --><?php get_sidebar(); ?><?php get_footer(); ?>